import { Injectable } from '@angular/core';
import { Pokemon } from '../../models/pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokemonInfService {
  pokemons: Pokemon[];
  constructor() {
    this.pokemons = [
    /*   {
        pokemonNumber: 1,
        pokemonNumberByRegion: 2,
        pokemonName: 'Ara',
        pokemonType: 'Agua'
      },
      {
        pokemonNumber: 124,
        pokemonNumberByRegion: 543,
        pokemonName: 'Flamita',
        pokemonType: 'Fuego'
      },      
      {
        pokemonNumber: 239,
        pokemonNumberByRegion: 263,
        pokemonName: 'Terra',
        pokemonType: 'Tierra'
      },
      {
        pokemonNumber: 19,
        pokemonNumberByRegion: 19,
        pokemonName: 'Andrés',
        pokemonType: 'Fuego'
      }, */
    ];
  }

  getPokemon () {
    let pokemonslocal = [];
    pokemonslocal = JSON.parse(localStorage.getItem('pokemon')||'');
    return pokemonslocal;
  }

  addPokemon(pokemon: Pokemon) {
    let pokemonsLS = [];
    pokemonsLS.push(pokemon)
    console.log(pokemonsLS);
    localStorage.setItem('pokemon', JSON.stringify(this.pokemons));
  }

  getPokemonById () {
    this.pokemons.filter((pokemon)=>pokemon.pokemonNumber == 19)
  }
}
