import { Component, OnInit } from '@angular/core';
import { PokemonInfService } from 'src//services/pokemonInfService/pokemon-inf.service';
import { Pokemon } from 'src/app/models/pokemon';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-pokemons-details',
  templateUrl: './pokemons-details.component.html',
  styleUrls: ['./pokemons-details.component.css']
})
export class PokemonsDetailsComponent implements OnInit {
  pokemons: Pokemon[] = [];
  pokemonId: number;

  constructor(
    private route: ActivatedRoute,
    public pokemonService: PokemonInfService
  ) { }

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.pokemonId = Number(params['id']);
      /* this.pokemons = this.pokemonService.getPokemonById(); */
    });
  }
}
