import { Injectable } from '@angular/core';
import { pokemon } from '../../models/pokemon';

@Injectable({
  providedIn: 'root'
})
export class PokemonInfService {
  pokemons: pokemon[];
  constructor() {
    this.pokemons  = [
      {
        pokemonNumber: 1,
        pokemonNumberByRegion: 2,
        pokemonName: 'Ara',
        pokemonType: 'Agua'
      },
      {
        pokemonNumber: 124,
        pokemonNumberByRegion: 543,
        pokemonName: 'Flamita',
        pokemonType: 'Fuego'
      },      {
        pokemonNumber: 239,
        pokemonNumberByRegion: 263,
        pokemonName: 'Terra',
        pokemonType: 'Tierra'
      },
    ];
  }

  getPokemon () {
    return this.pokemons;
  }

  addPokemon (pokemons: pokemon) {
    this.pokemons.push(pokemon);
  }
}
